package ebs

import (
	"github.com/crossplane/upjet/pkg/config"

	pconfig "github.com/upbound/provider-aws/config"
)

// Configure adds configurations for the ebs group.
func Configure(p *config.Provider) {
	p.AddResourceConfigurator("aws_ebs_volume", func(r *config.Resource) {
		r.References = map[string]config.Reference{
			"kms_key_id": {
				Type: "github.com/upbound/provider-aws/apis/kms/v1beta1.Key",
			},
		}
	})
}

func init() {
	pconfig.ProviderConfiguration.AddConfig(Configure)
}
